package 변수.copy;

public class Primitive_Data {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 정수, 실수, 문자, 논리 >> 
		// ram 에 넣어보자
		int age = 100; //정수(4)
		double height = 122.2; // 실수(8)
		char gender = '남'; //문자 1개 >>> ''(홑따옴표) 문자(2)
		boolean  food = true; // 논리 (1)
		// 앞에다가 형식을 붙여줌
		System.out.println("나의 나이는 " + age);
		System.out.println("나의 키는 " + height);
		System.out.println("나의 성별은 " + gender);
		System.out.println("내가 밥 먹은 여부는  " + food);
	}

}
