package operator;

public class Comparison_Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 200;
		int y = 100;
		
		
		// alert(), print()
		System.out.println("일치?? " + ( x == y ));
		System.out.println("일치?? " + ( x != y ));
		System.out.println("일치?? " + ( x >= y ));
		System.out.println("일치?? " + ( x > y ));
	}

}
